                webApp: u.indexOf('Safari') == -1, //是否web应用程序，没有头部与底部
                UCB: u.match(/UCBrowser/i) == "UCBrowser",
                QQB: u.match(/MQQBrowser/i) == "MQQBrowser",  //安卓QQ客户端包括QQ浏览器
