                win: p.indexOf('Win') > -1,//判断是否是WIN操作系统
                mac: p.indexOf('Mac') > -1,//判断是否是Mac操作系统
                weibo:ua.match(/weibo/i) == "weibo",
                zhefengle:ua.match(/zhefengle/i) == 'zhefengle', //判断是否是自己app
